"""Policy interface for SudoAgent."""

from __future__ import annotations

from typing import Protocol

from pydantic import BaseModel, field_validator

from .reason_codes import POLICY_ALLOW_LOW_RISK, POLICY_DENY_HIGH_RISK
from .types import Context, Decision


class PolicyResult(BaseModel):
    """Result of policy evaluation without request ID."""

    model_config = {"frozen": True}

    decision: Decision
    reason: str
    reason_code: str | None = None

    @field_validator("reason")
    @classmethod
    def _reason_non_empty(cls, value: str) -> str:
        if not value or not value.strip():
            raise ValueError("reason must be a non-empty string")
        return value

    @field_validator("reason_code")
    @classmethod
    def _reason_code_non_empty(cls, value: str | None) -> str | None:
        if value is None:
            return None
        if not value.strip():
            raise ValueError("reason_code, if provided, must be non-empty")
        return value


class Policy(Protocol):
    """Policy interface for evaluating guarded actions."""

    def evaluate(self, ctx: Context) -> PolicyResult:
        """Evaluate the context and return a policy decision."""
        ...


class AllowAllPolicy:
    """Policy that allows all actions."""

    def evaluate(self, ctx: Context) -> PolicyResult:
        return PolicyResult(
            decision=Decision.ALLOW,
            reason="allowed",
            reason_code=POLICY_ALLOW_LOW_RISK,
        )


class DenyAllPolicy:
    """Policy that denies all actions."""

    def evaluate(self, ctx: Context) -> PolicyResult:
        return PolicyResult(
            decision=Decision.DENY,
            reason="denied",
            reason_code=POLICY_DENY_HIGH_RISK,
        )
